import React, { useState, Component } from 'react';
import {Router, Route, Link} from 'react-router-dom';
import {
    ButtonToggle,
    Button,
    ButtonGroup,
    ButtonToolbar,
    Form,
    FormGroup,
    Label,
    Input,
    Col,
    Row
} from 'reactstrap';
import HeaderMentor from '../Components/HeaderMentor';
import PageRouter from '../Components/PageRouter';
import midamLogo from '../midam.png';
import '../Css/login.css'
import { render } from '@testing-library/react';

class Login extends Component {
    constructor(props){
        super(props);

        this.state = {
            users: [],
            message: null,
        }
    }

    render() {
        return (
            <div class="login_container " className = "d-flex justify-content-center align-self-center" style = {{margin : "100px"}}>
                <Form>
                    <div class = "logo">
                        <img src={midamLogo} art="midam"></img>
                        <h3>미담장학회 멘토 관리 시스템</h3>
                        <hr class = "under_line"></hr>
                    </div>
                    <FormGroup>
                        
                        <ButtonGroup className = "w-100" style = {{marginBottom : '5px'}}>
                            <Button className = "w-50" color="secondary">멘토</Button>
                            <Button className = "w-50" color="secondary">기관 담당자</Button>
                        </ButtonGroup>
    
                        <div className = "d-flex justify-content-around align-self-center">
                            <Input name="id" id="loginId" placeholder="아이디를 입력하세요"
                                style = {{marginBottom : '5px'}}/>    
                        </div>
    
                        <div className = "d-flex justify-content-around align-self-center">
                            <Input
                                type="password"
                                name="password"
                                id="loginPassword"
                                placeholder="비밀번호를 입력하세요"
                                style = {{marginBottom : '5px'}}
                                />
                        </div>
    
                        <Button className = "w-100">로그인</Button>
                        
                        <hr class = "under_line"></hr>
    
                    </FormGroup>
                </Form>
            </div>
        )
    }
}

export default Login;
